#!/usr/bin/env python3# coding: utf-8import requestsimport sysfrom time import time### Exercice 1#Questions 1&2def fetch(URL, id):    t1 = time()    r = requests.get(URL)    t2 = time() - t1    code_retour_HTTP = r.status_code # vérifie le code de retour d’une réponse    nombre_octets = sys.getsizeof(r.text)     temps_requete = t2    print(id," ",code_retour_HTTP, " ",nombre_octets, " ",temps_requete)    return t2### Exercice 2# r = requests.get('https://jsonplaceholder.typicode.com', verify=True)# print(r.text)# print(r.headers)# print(r.encoding)if __name__=="__main__":    t = time()    n = 10    cumul_temps = 0    URL = ["https://math.univ-angers.fr",           "http://www.univ-angers.fr",           "http://httpbin.org/get",           "https://math.univ-angers.fr/~jaclin/2020ds2/python_parallelisme/2020/aiohttp/collecte.html"]    for i, url in enumerate(URL):        for n in range(n):            n += 1            cumul_temps += fetch(url,n)    print("cumul des temps mesurés: ", cumul_temps)    print("temps global d’exécution du programme: ", time()-t)