#!/bin/env python3import aiohttpimport asyncioimport async_timeoutimport timeimport sysURL = "https://math.univ-angers.fr/~jaclin/wait.php"url = 'https://math.univ-angers.fr/~jaclin/wait.php?t='urls = [url + str(i) for i in range(0,10)]def usage():  print(f"usage: {sys.argv[0]} tempo")  exit(1)async def fetch(url):  async with aiohttp.ClientSession() as session:    try:      async with async_timeout.timeout(10):        async with session.get(url) as response:          if response.status==200:              print(await response.read())          else:              print(f"retcode {response.status}")    except asyncio.TimeoutError:      print("interrompu, car trop long à répondre")try:    tempo = int(sys.argv[1])except Exception:    usage()loop = asyncio.get_event_loop()loop.run_until_complete(fetch(f"{URL}?t={tempo}"))loop.close()